      <SevenDayComponent />
      <HorizontalLine />
      <SevenDayComponent />
      <HorizontalLine />
      <SevenDayComponent />
      <HorizontalLine />
      <SevenDayComponent />
      <HorizontalLine />
      <SevenDayComponent />
