import React,{useState,useEffect} from "react";
import axios from 'axios';
import "./main.css";
import * as openWeatherApi from "../API'S/openWeatherApi"
import useIPLocation from "../API'S/IPLocation ";
import SideBar from "./Sidebar";
import SearchBar from "./SearchBar";
import SunnyImg from "../images/Sunny.png";
import ThermometerImg from "../images/Thermometer.png";
import WindImg from "../images/Winds.png";
import DropImg from "../images/RainDrop.png";
import SunImg from "../images/Sun.png";


export default function Main() {
  const [weatherData, setWeatherData] = useState(null);
  const [forecastData, setForecastData] = useState(null);
  const [loading, setLoading] = useState(true);

  const locationData = useIPLocation();

  useEffect(() => {
    const fetchInitialWeather = async () => {
      if (locationData) {
        try {
          const { lat, lon } = locationData;
          const [weather, forecast] = await openWeatherApi.fetchWeatherData(
            lat,
            lon
          );
          setWeatherData(weather);
          setForecastData(forecast);
        } catch (error) {
          console.error("Error fetching initial weather data:", error);
        } finally {
          setLoading(false);
        }
      }
    };

    fetchInitialWeather();
  }, [locationData]);

  const handleCityNameChange = async (city) => {
    const { latitude, longitude } = city;
    try {
      setLoading(true);
      const [weather, forecast] = await openWeatherApi.fetchWeatherData(
        latitude,
        longitude
      );
      setWeatherData(weather);
      setForecastData(forecast);
    } catch (error) {
      console.error("Error fetching weather data", error);
      setWeatherData(null);
      setForecastData(null);
    } finally {
      setLoading(false);
    }
  };
  
  return (
    <div className="flex">
      <div className="flex-1">
        <SideBar />
      </div>
      <div className="flex-2">
        <SearchBar handleCityNameChange={handleCityNameChange} />
        <Temperature weatherData={weatherData} loading={loading} />
        <TodaysForecast />
        <Airconditions />
      </div>
      <div className="flex-3">
        <SevenDayForecast />
      </div>
    </div>
  );
}

function Temperature({ weatherData, loading }) {
  const cityName = loading ? "Loading..." : weatherData?.name || "Unknown Location";
  const chanceOfRain = loading ? "-" : weatherData?.rain ? `${weatherData.rain["1h"]}%` : "0%";
  const temperature = loading ? "-" : weatherData?.main ? `${Math.round(weatherData.main.temp)}Â°C` : "-";
  
  return (
    <div className="temp-box">
      <div className="statistics-box">
        <div className="city-box">
          <span className="city-text">{cityName}</span>
          <span className="chance-text">Chance of rain: {chanceOfRain}</span>
          <span className="degree-text">{temperature}</span>
        </div>
      </div>
      <div className="img-box">
        <img
          src={SunnyImg} // Ensure you have SunnyImg imported or defined
          alt="weather-condition"
          className="cond-img"
        />
      </div>
    </div>
  );
}


function TodaysForecast() {
  return (
    <div className="todays-forecast-box">
      <div className="forecast-text-box">
        <span className="forecast-text">TODAY'S FORECAST</span>
        <div className="big-forecast-box">
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
        </div>
      </div>
    </div>
  );
}
function VerticalLine(){
    return (
        <div className="vertical-line"></div>
    );
}

function ForecastComponent() {
  return (
    <div className="forecast-box">
      <span className="time">6:00 AM</span>
      <img src={SunnyImg} alt="img.." className="small-cond-img" />
      <span className="forecast-temp">25&deg;</span>
    </div>
  );
}

function Airconditions() {
  return (
    <div className="air-condition-box">
      <div className="one-flex">
        <span className="air-condition-text">AIR CONDITIONS</span>
        <AirconditionComponent
          icon={ThermometerImg}
          text="Real Feel"
          statistics="30&deg;"
        />
        <AirconditionComponent
          icon={DropImg}
          text="Chance of rain"
          statistics="0%;"
        />
      </div>
      <div className="tp-flex">
        
      </div>
      <div className="two-three-main-flex">
        <div className="two-flex">
          <AirconditionComponent
