import ClearSkyNight from "../../images/icons"
export default function getWeatherIcon(iconCode) {
  let iconUrl;

  switch (iconCode) {
    case "01d":
      iconUrl = SunnyImg;
      break;
    case "01n":
      iconUrl = ClearSkyNight;
      break;
    case "02d":
      iconUrl = FewCloudsDay;
      break;
    case "02n":
      iconUrl = FewCloudsNight;
      break;
    case "03d":
    case "03n":
      iconUrl = ScatteredClouds;
      break;
    case "04d":
    case "04n":
      iconUrl = BrokenClouds;
      break;
    case "09d":
    case "09n":
      iconUrl = ShowerRain;
      break;
    case "10d":
      iconUrl = RainDay;
      break;
    case "10n":
      iconUrl = RainNight;
      break;
    case "11d":
    case "11n":
      iconUrl = Thunderstorm;
      break;
    case "13d":
    case "13n":
      iconUrl = Snow;
      break;
    case "50d":
    case "50n":
      iconUrl = Mist;
      break;
    default:
      iconUrl = UnknownWeather;
      break;
  }

  return iconUrl;
}
