import React, { useState } from "react";
import axios from "axios";
export default function SearchBar({handleCityNameChange}) {
  const [query,setQuery] = useState('');
  const [suggestions, setSuggestions] = useState([]);

  const handleInputChange =(input) => {
    setQuery(input);
    fetchSuggestions(input);
  }

  const fetchSuggestions = async (input) => {
    if(input.length < 3){
      setSuggestions([]);
      return;
    }

    try{
      const response = await axios.get();
      const cityNames = response.data.map(city=> city.name);
      setSuggestions(cityNames);
    } catch(error){
      console.log('Error fetching city suggestions: ',error);
    }
  };

  const handleSuggestionClick = async (suggestion) =>{
    setQuery(suggestion);
    setSuggestions([]);
    handleCityNameChange(suggestion);
  };


  return (
    <div className="searchbar-div">
      <input 
      type="text" 
      value={query}
      onChange={(e)=> handleInputChange(e.target.value)}
      placeholder="Search for cities" className="search-bar"/>
      <ul>
        {suggestions.map((suggestion,index)=>{
          <li key={index} onClick={()=> handleSuggestionClick(suggestion)} className="suggestion-list-element">
            {suggestion}
          </li>
        })}
      </ul>
    </div>
  
  );
}
