import React, { useState } from "react";
import axios from "axios";

const GEO_API_URL = "https://wft-geo-db.p.rapidapi.com/v1/geo";
const GEO_API_OPTIONS = {
  method: "GET",
  headers: {
    "X-RapidAPI-Key": "your-api-key",
    "X-RapidAPI-Host": "wft-geo-db.p.rapidapi.com",
  },
};

export default function SearchBar({ handleCityNameChange }) {
  const [query, setQuery] = useState("");
  const [suggestions, setSuggestions] = useState([]);
  const [error, setError] = useState(null);

  const handleInputChange = async (input) => {
    setQuery(input);
    if (input.length < 3) {
      setSuggestions([]);
      return;
    }

    try {
      const response = await axios.get(
        `${GEO_API_URL}/cities?minPopulation=10000&namePrefix=${input}`,
        GEO_API_OPTIONS
      );

      const cities = response.data.data.map((city) => ({
        name: city.name,
        countryCode: city.countryCode,
        latitude: city.latitude,
        longitude: city.longitude,
      }));

      setSuggestions(cities);
      setError(null); // Clear error state if successful
    } catch (error) {
      if (error.response && error.response.status === 403) {
        setError("Forbidden: Check your API key or permissions.");
      } else {
        console.log("Error fetching city suggestions: ", error);
        setError("Error fetching city suggestions. Please try again later.");
      }
      setSuggestions([]);
    }
  };

  // Rest of your component code

  return (
    <div className="searchbar-div">
      <input
        type="text"
        value={query}
        onChange={(e) => handleInputChange(e.target.value)}
        placeholder="Search for cities"
        className="search-bar"
      />
      {error && <p>{error}</p>}
      <ul>
        {suggestions.map((suggestion, index) => (
          <li
            key={index}
            onClick={() =>
              handleCityNameChange(
                suggestion.name,
                suggestion.latitude,
                suggestion.longitude
              )
            }
            className="suggestion-list-element"
          >
            {suggestion.name}, {suggestion.countryCode}
          </li>
        ))}
      </ul>
    </div>
  );
}
