import React,{useState,useEffect} from "react";
import axios from 'axios';
import "./main.css";
import * as openWeatherApi from "../API'S/openWeatherApi"
import IPLocation from "../API'S/IPLocation ";
import SideBar from "./Sidebar";
import SearchBar from "./SearchBar";
import SunnyImg from "../images/Sunny.png";
import ThermometerImg from "../images/Thermometer.png";
import WindImg from "../images/Winds.png";
import DropImg from "../images/RainDrop.png";
import SunImg from "../images/Sun.png";

export default function Main() {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch initial weather data based on IP location
    const fetchInitialWeather = async () => {
      try {
        const ipLocation = await IPLocation();

        if (ipLocation) {
          const lat = ipLocation.data.lat;
          const lon = ipLocation.lon;
          const data = await openWeatherApi.fetchWeatherData(city);
          setWeatherData(data);
        }
      } catch (error) {
        console.error("Error fetching initial weather data:", error);
      } finally {
        setLoading(false);
      }
    };

    fetchInitialWeather();
  }, []);

  // Function to handle city name input change
  const handleCityNameChange = async (cityName) => {
    try {
      setLoading(true);
      const data = await openWeatherApi.fetchWeatherByCityName(cityName);
      setWeatherData(data);
    } catch (error) {
      console.error("Error fetching weather data by city name:", error);
      setWeatherData(null);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="flex">
      <div className="flex-1">
        <SideBar />
      </div>
      <div className="flex-2">
        <SearchBar handleCityNameChange={handleCityNameChange} />
        <Temperature />
        <TodaysForecast />
        <Airconditions />
      </div>
      <div className="flex-3">
        <SevenDayForecast />
      </div>
    </div>
  );
}

function Temperature({ weatherData, loading }) {
  return (
    <div className="temp-box">
      <div className="statistics-box">
        <div className="city-box">
          <span className="city-text">
            {loading
              ? "Loading..."
              : weatherData.name
              }
          </span>
          <span className="chance-text">
            Chance of rain: {loading ? "-" : "10%" ? "0%" : "-"}
          </span>
          <span className="degree-text">
            {loading
              ? "-"
              : weatherData
              ? `${Math.round(weatherData.main.temp)}Â°C`
              : "-"}
          </span>
        </div>
      </div>
      <div className="img-box">
        <img
          src={SunnyImg}
          alt="weather-condition loading.."
          className="cond-img"
        />
      </div>
    </div>
  );
}

function TodaysForecast() {
  return (
    <div className="todays-forecast-box">
      <div className="forecast-text-box">
        <span className="forecast-text">TODAY'S FORECAST</span>
        <div className="big-forecast-box">
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
          <VerticalLine />
          <ForecastComponent />
        </div>
      </div>
    </div>
  );
}
function VerticalLine(){
    return (
        <div className="vertical-line"></div>
    );
}

function ForecastComponent() {
  return (
    <div className="forecast-box">
      <span className="time">6:00 AM</span>
      <img src={SunnyImg} alt="img.." className="small-cond-img" />
      <span className="forecast-temp">25&deg;</span>
    </div>
  );
}

function Airconditions() {
  return (
    <div className="air-condition-box">
      <div className="one-flex">
        <span className="air-condition-text">AIR CONDITIONS</span>
        <AirconditionComponent
          icon={ThermometerImg}
          text="Real Feel"
          statistics="30&deg;"
        />
        <AirconditionComponent
          icon={DropImg}
          text="Chance of rain"
          statistics="0%;"
        />
      </div>
      <div className="tp-flex">
        
      </div>
      <div className="two-three-main-flex">
        <div className="two-flex">
          <AirconditionComponent
            icon={WindImg}
            text="Wind"
            statistics="0.2 km/h"
          />
          <AirconditionComponent icon={SunImg} text="UV Index" statistics="3" />
        </div>
        <div className="three-flex">
          <button className="see-more-btn">See more</button>
        </div>
      </div>
    </div>
  );
}

function AirconditionComponent({ icon, text, statistics }) {
  return (
    <div className="real-feel-box">
      <div className="real-feel-img-box">
        <img src={icon} className="feel-img" />
      </div>
      <div className="real-feel-statistics">
